import React from 'react';
import PropTypes from 'prop-types';
import { render } from 'react-dom';

import BrowserRouter from 'react-router-dom/BrowserRouter';
import { renderRoutes } from 'react-router-config';
import { createStore, applyMiddleware } from 'redux';
import thunk from 'redux-thunk';
import { Provider } from 'react-redux';

import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';

import reducers from './modules';

const store = createStore(reducers, window.__INITIAL_STATE__, applyMiddleware(thunk));
delete window.__INITIAL_STATE__;

const AppRouter = ({ routes }) => (
  <Provider store={store}>
    <BrowserRouter>
      { renderRoutes(routes) }
    </BrowserRouter>
  </Provider>
);

const routeShape = PropTypes.shape({
  component: PropTypes.func.isRequired,
  path: PropTypes.string,
  exact: PropTypes.bool,
});
routeShape.routes = PropTypes.arrayOf(routeShape);

AppRouter.propTypes = {
  routes: PropTypes.arrayOf(routeShape).isRequired,
};

function renderApp() {
  // eslint-disable-next-line global-require
  render(<AppRouter routes={require('./routes').default} />, document.querySelector('#app'));
}

renderApp();
if (module.hot) module.hot.accept('./routes', renderApp);

import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import pluralize from 'pluralize';
import { createDocument, updateDocument, deleteDocument } from '../modules/modelData';
import { postNotification, clearNotification, SUCCESS, INFO, ERROR } from '../modules/notifications';

class EditForm extends Component {
  constructor(props) {
    super(props);
    this.state = props.document || {};
    this.cancelHandler = this.handleCancel.bind(this);
    this.deleteHandler = this.handleDelete.bind(this);
    this.submitHandler = this.handleSubmit.bind(this);
  }

  componentWillReceiveProps(nextProps) {
    this.setState(nextProps.document || {});
  }

  hasChanges() {
    let changed = false;
    const document = this.props.document || {};
    Object.keys(this.state).forEach((key) => {
      if (this.state[key] !== document[key]) {
        changed = true;
      }
    });
    return changed;
  }

  handleCancel(e) {
    e.preventDefault();
    return (
      (!this.hasChanges() || confirm('Are you sure you want to cancel?')) &&
      this.props.onClose &&
      this.props.onClose()
    );
  }

  handleDelete(e) {
    e.preventDefault();
    if (!this.props.id) {
      return false;
    }
    const deletingNotification = this.props.postNotification(INFO, 'Deleting...');
    return this.props.delete()
      .then(() => {
        this.props.clearNotification(deletingNotification.key);
        this.props.postNotification(SUCCESS, 'Deleted successfully!');
        return this.props.onClose();
      })
      .catch(err => err.json())
      .then((error) => {
        if (error) {
          this.props.clearNotification(deletingNotification.key);
          this.props.postNotification(ERROR, error.message);
        }
      });
  }

  handleSubmit(e) {
    e.preventDefault();
    const savingNotification = this.props.postNotification(INFO, 'Saving...');
    return (this.props.id ?
      this.props.update(this.state) :
      this.props.create(this.state)
    ).then(() => {
      this.props.clearNotification(savingNotification.key);
      this.props.postNotification(SUCCESS, 'Saved successfully!');
      return this.props.onClose();
    })
      .catch(err => err.json())
      .then((error) => {
        if (error) {
          this.props.clearNotification(savingNotification.key);
          this.props.postNotification(ERROR, error.message);
        }
      });
  }

  render() {
    return (
      <form className="form" key={this.props.id && this.props.document} onSubmit={this.submitHandler}>
        {this.props.fields.map(field => (
          <div key={field.key} className="form-group">
            <field.editorComponent
              label={field.key}
              field={field}
              value={this.state[field.key] || ''}
              valueChanged={(value) => {
                this.setState({
                  [field.key]: value,
                });
              }}
            />
          </div>
        ))}
        <div className="btn-toolbar">
          <button disabled={!this.props.id} className="btn btn-danger" onClick={this.deleteHandler}>Delete</button>
          <button className="btn btn-light" onClick={this.cancelHandler}>Cancel</button>
          <button className="btn btn-primary pull-right" type="submit">Save</button>
        </div>
      </form>
    );
  }
}

EditForm.propTypes = {
  id: PropTypes.string,
  // eslint-disable-next-line react/forbid-prop-types
  document: PropTypes.object,
  fields: PropTypes.arrayOf(PropTypes.shape({
    key: PropTypes.string.isRequired,
    editorComponent: PropTypes.func.isRequired,
  })).isRequired,
  create: PropTypes.func.isRequired,
  update: PropTypes.func.isRequired,
  delete: PropTypes.func.isRequired,
  clearNotification: PropTypes.func.isRequired,
  postNotification: PropTypes.func.isRequired,
  onClose: PropTypes.func.isRequired,
};

EditForm.defaultProps = {
  id: undefined,
  document: {},
};

export default connect((state, ownProps) => {
  const modelData = state.modelData[pluralize(ownProps.modelName)];
  return {
    document: modelData && modelData.__DB__[ownProps.id],
  };
}, (dispatch, ownProps) => (
  {
    create(document) {
      return dispatch(createDocument(ownProps.modelName, document));
    },
    update(document) {
      return dispatch(updateDocument(ownProps.modelName, ownProps.id, document));
    },
    delete() {
      return dispatch(deleteDocument(ownProps.modelName, ownProps.id));
    },
    postNotification(status, message) {
      return dispatch(postNotification(status, message));
    },
    clearNotification(key) {
      return dispatch(clearNotification(key));
    },
  }
))(EditForm);

import React, { Component } from 'react';
import PropTypes from 'prop-types';
import classnames from 'classnames';
import withTitle from '../../client/hoc/withTitle';

class Modal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      hidden: true,
    };
    this.closeHandler = this.handleClose.bind(this);
  }

  componentDidMount() {
    this.componentWillReceiveProps(this.props);
  }

  componentWillReceiveProps(nextProps) {
    if (nextProps.open !== this.state.open) {
      if (nextProps.open) {
        this.open();
      } else {
        this.close();
      }
    }
  }

  componentDidUpdate(prevProps) {
    if (this.props.title !== prevProps.title) {
      this.props.setTitle();
    }
  }

  handleClose() {
    if (!this.props.confirmClose || this.props.confirmClose()) {
      if (this.props.onClose) {
        this.props.onClose();
      }
      this.close();
    }
  }

  open() {
    this.setState({
      hidden: false,
    }, () => {
      process.nextTick(() => {
        this.setState({
          open: true,
        });
      });
    });
  }

  close() {
    this.setState({
      open: false,
    }, () => {
      setTimeout(() => {
        this.setState({
          hidden: true,
        });
      }, 300);
    });
  }

  render() {
    return (
      <div className={classnames({ 'modal-open': !this.state.hidden })}>
        <div
          className={classnames('modal fade', {
            show: this.state.open,
          })}
          style={{
            display: this.state.hidden ? 'none' : 'block',
          }}
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h4 className="modal-title">{this.props.title}</h4>
                <button type="button" className="close" onClick={this.closeHandler}><span>&times;</span></button>
              </div>
              <div className="modal-body">
                {this.props.children}
              </div>
              { this.props.footer ?
                <div className="modal-footer">
                  {this.props.footer}
                </div>
              : null }
            </div>
          </div>
        </div>
      </div>
    );
  }
}

Modal.propTypes = {
  open: PropTypes.bool,
  title: PropTypes.string,
  setTitle: PropTypes.func.isRequired,
  confirmClose: PropTypes.func,
  onClose: PropTypes.func,
  children: PropTypes.node,
  footer: PropTypes.node,
};

Modal.defaultProps = {
  open: false,
  title: '',
  confirmClose: undefined,
  onClose: undefined,
  children: null,
  footer: null,
};

export default withTitle(ownProps => ownProps.title)(Modal);

import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import classnames from 'classnames';
import { clearNotification, SUCCESS, INFO, WARNING, DANGER } from '../modules/notifications';

import styles from './Notifications.css';

function alertClass(status) {
  switch (status) {
    case SUCCESS:
      return 'alert-success';
    case WARNING:
      return 'alert-warning';
    case DANGER:
      return 'alert-danger';
    case INFO:
    default:
      return 'alert-info';
  }
}

class Notification extends Component {
  constructor(props) {
    super(props);
    this.state = {
      in: false,
    };
    this.closeHandler = this.handleClose.bind(this);
  }

  componentDidMount() {
    this.mountTimeout = setTimeout(() => {
      this.setState({
        in: true,
      });
    }, 0);

    this.autoCloseTimeout = setTimeout(this.closeHandler, 5000);
  }

  componentWillUnmount() {
    clearTimeout(this.mountTimeout);
    clearTimeout(this.autoCloseTimeout);
  }

  handleClose() {
    clearTimeout(this.autoCloseTimeout);
    this.setState({
      in: false,
    }, () => {
      setTimeout(this.props.clear, 300);
    });
  }

  render() {
    return (
      <div className={classnames(
        'alert fade alert-dismissable',
        alertClass(this.props.notification.status),
        { show: this.state.in },
      )}>
        <button className="close" onClick={this.closeHandler}>&times;</button>
        {this.props.notification.message}
      </div>
    );
  }
}

Notification.propTypes = {
  notification: PropTypes.shape({
    status: PropTypes.oneOf([SUCCESS, INFO, WARNING, DANGER]),
    message: PropTypes.string,
  }).isRequired,
};

const Notifications = props => (
  <div className={styles.notifications}>
    <div className="container">
      {Object.keys(props.notifications).map(k => (
        <Notification
          key={k}
          notification={props.notifications[k]}
          clear={() => props.clearNotification(k)}
        />
      ))}
    </div>
  </div>
);

Notifications.propTypes = {
  notifications: PropTypes.objectOf(Notification.propTypes.notification).isRequired,
};

export default connect(state => (
  {
    notifications: state.notifications || {},
  }
), dispatch => (
  {
    clearNotification(key) {
      return dispatch(clearNotification(key));
    },
  }
))(Notifications);

import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Promise from 'bluebird';

import styles from './TableView.css';

class TableView extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.resizeHandler = this.onWindowResize.bind(this);
    this.mouseUpHandler = this.handleMouseUp.bind(this);
    this.mouseMoveHandler = this.handleMouseMove.bind(this);
    this.columnsDirty = true;

    this.resizing = false;
    this.resizeStartX = undefined;
  }

  getChildContext() {
    return {
      table: this,
    };
  }

  componentDidMount() {
    this.setColumnWidths();
    window.addEventListener('resize', this.resizeHandler);
  }

  componentWillUnmount() {
    window.removeEventListener('resize', this.resizeHandler);
  }

  onWindowResize() {
    this.columnsDirty = true;
  }

  setColumnWidths() {
    this.columnsDirty = false;
    return Promise.map(this.props.columns, (column) => {
      const cell = this[`${column.key}-header`];
      if (!cell) {
        return Promise.resolve();
      }
      const el = cell.cell;
      return new Promise((resolve) => {
        this.setState({
          [`${column.key}-width`]: `${el.clientWidth}px`,
        }, resolve);
      });
    });
  }

  initializeColumnResize(column, startX) {
    this.setColumnWidths().then(() => {
      this.resizeStartX = startX;
      this.resizingColumns = [
        column,
        this.props.columns[this.props.columns.findIndex(col => (col.key === column)) - 1].key,
      ];

      this.initialWidths = this.resizingColumns.map(col => this[`${col}-header`].refs.cell.clientWidth);
      this.resizing = true;
    });
  }

  handleMouseMove(e) {
    if (this.resizing) {
      this.setState({
        [`${this.resizingColumns[0]}-width`]: `${this.initialWidths[0] - (e.screenX - this.resizeStartX)}px`,
        [`${this.resizingColumns[1]}-width`]: `${this.initialWidths[1] + (e.screenX - this.resizeStartX)}px`,
      });
      if (e.buttons !== 1) {
        this.finishColumnResize();
      }
    }
  }

  handleMouseUp() {
    process.nextTick(() => this.finishColumnResize());
  }

  finishColumnResize() {
    this.resizing = false;
  }

  render() {
    return (
      <div
        ref={(el) => { this.tabel = el; }}
        className={styles['table-view']}
        onMouseMove={this.mouseMoveHandler}
        onMouseUp={this.mouseUpHandler}
      >
        <TableRow>
          {this.props.columns.map(column => (
            <TableCell ref={(el) => { this[`${column.key}-header`] = el; }} key={column.key} column={column.key} initialSize={column.initialSize}>
              <strong>{column.key}</strong>
            </TableCell>
          ))}
        </TableRow>
        {React.Children.map(this.props.children, child => (
          React.cloneElement(child, {
            onClick: child.props.onClick && ((e) => {
              if (this.resizing) {
                e.preventDefault();
                e.stopPropagation();
              } else {
                child.props.onClick(e);
              }
            }),
          })
        ))}
      </div>
    );
  }
}

TableView.childContextTypes = {
  table: PropTypes.instanceOf(TableView),
};

TableView.propTypes = {
  children: PropTypes.node,
  columns: PropTypes.arrayOf(PropTypes.shape({
    key: PropTypes.string.isRequired,
    initialSize: PropTypes.string.isRequired,
  })).isRequired,
};

TableView.defaultProps = {
  children: null,
};

class TableRow extends Component {
  render() {
    return (
      <div className={styles['table-row']} {...this.props}>
        {this.context.table.props.columns.map((column) => {
          const cell = React.Children.toArray(this.props.children)
            .find(child => child.props.column === column.key);
          if (!cell) {
            return <TableCell key={column.key} column={column.key} />;
          }
          return React.cloneElement(cell, {
            onClick: cell.props.onClick && ((e) => {
              if (this.context.table.resizing) {
                e.preventDefault();
                e.stopPropagation();
              } else {
                cell.props.onClick(e);
              }
            }),
          });
        })}
      </div>
    );
  }
}

TableRow.contextTypes = {
  table: PropTypes.instanceOf(TableView),
};

TableRow.propTypes = {
  children: PropTypes.node,
};

TableRow.defaultProps = {
  children: null,
};

class TableCell extends Component {
  constructor(props) {
    super(props);
    this.mouseDownHandler = this.handleMouseDown.bind(this);
  }

  handleMouseDown(e) {
    this.context.table.initializeColumnResize(this.props.column, e.screenX);
  }

  render() {
    const { column, initialSize, ...props } = this.props;
    const basis = (this.context.table.state[`${column}-width`] || initialSize);
    return (
      <div
        ref={(el) => { this.cell = el; }}
        className={`${styles['table-cell']} ${column}-column`}
        style={{
          flexBasis: basis,
        }}
        {...props}
      >
        <div
          className={`${styles['resize-handle']} ${styles['resize-handle-left']}`}
          onMouseDown={this.mouseDownHandler}
        />
        {this.props.children}
      </div>
    );
  }
}

TableCell.contextTypes = {
  table: PropTypes.instanceOf(TableView),
};

TableCell.propTypes = {
  children: PropTypes.node,
  column: PropTypes.string.isRequired,
  initialSize: PropTypes.string.isRequired,
};

TableCell.defaultProps = {
  children: null,
};

export default TableView;
export { TableRow, TableCell };

import React from 'react';
import PropTypes from 'prop-types';

/**
 * Checkbox for display a boolean value
 * @param {Object} props
 * @param {boolean} [props.value=false] The boolean value
 * @return {ReactElement}
 */
const BooleanDisplay = props => (props.value ? <i className="fa fa-check" aria-hidden="true" /> : null);

BooleanDisplay.propTypes = {
  value: PropTypes.bool,
};

BooleanDisplay.defaultProps = {
  value: false,
};

export default BooleanDisplay;

import PropTypes from 'prop-types';
import moment from 'moment';

const DateDisplay = props => moment(props.value).format('ll');

DateDisplay.propTypes = {
  value: PropTypes.oneOfType([
    PropTypes.instanceOf(Date),
    PropTypes.string,
  ]),
};

DateDisplay.defaultProps = {
  value: '',
};

export default DateDisplay;

import PropTypes from 'prop-types';

const TextDisplay = props => props.value;

TextDisplay.propTypes = {
  value: PropTypes.string,
};

TextDisplay.defaultProps = {
  value: '',
};

export default TextDisplay;

import React from 'react';
import Field from './Field';

class BooleanField extends Field {
  render() {
    return (
      <div className="form-check">
        <input
          type="checkbox"
          className="form-check-input"
          checked={this.props.value}
          onChange={(e) => {
            this.props.valueChanged(e.target.checked);
          }}
        />
        <label className="form-check-label">{this.props.label}</label>
      </div>
    );
  }
}

export default BooleanField;

import React from 'react';
import DatePicker from 'react-datepicker';
import moment from 'moment';

import 'react-datepicker/dist/react-datepicker.css';
import './DateField.css';

import Field from './Field';

class DateField extends Field {
  render() {
    return (
      <div>
        <label>{this.props.label}</label>
        <DatePicker
          className="form-control"
          placeholder="Enter a date"
          selected={this.props.value && moment(this.props.value)}
          onChange={(date) => {
            this.props.valueChanged(date);
          }}
        />
      </div>
    );
  }
}

export default DateField;

