import { Component } from 'react';
import PropTypes from 'prop-types';

class Field extends Component { }

Field.propTypes = {
  value: PropTypes.any.isRequired,
  valueChanged: PropTypes.func.isRequired,
};

export default Field;

import React from 'react';
import Field from './Field';

class TextField extends Field {
  render() {
    return (
      <div>
        <label>{this.props.label}</label>
        <input
          type="text"
          className="form-control"
          placeholder={this.props.field.key}
          value={this.props.value}
          onChange={(e) => {
            this.props.valueChanged(e.target.value);
          }}
        />
      </div>
    );
  }
}

export default TextField;

import { combineReducers } from 'redux';
import metadata from '../../client/modules/metadata';
import modelData from './modelData';
import notifications from './notifications';

const rootReducer = combineReducers({
  metadata,
  modelData,
  notifications,
});

export default rootReducer;

import fetch from 'isomorphic-fetch';
import pluralize from 'pluralize';
import nconf from 'nconf';

export const MODEL_DATA_LOADED = 'MODEL_DATA_LOADED';
export const DOCUMENT_CREATED = 'DOCUMENT_CREATED';
export const DOCUMENT_UPDATED = 'DOCUMENT_UPDATED';
export const DOCUMENT_DELETED = 'DOCUMENT_DELETED';

const initialState = {};
const initialModelState = {
  __DB__: {},
  ids: [],
};

function model(state = initialModelState, action) {
  const DB = Object.assign({}, state.__DB__);

  switch (action.type) {
    case MODEL_DATA_LOADED:
      action.data.forEach((datum) => {
        Object.assign(DB, {
          [datum._id]: datum,
        });
      });

      return Object.assign({}, state, {
        __DB__: DB,
        ids: action.data.map(datum => datum._id),
      });

    case DOCUMENT_CREATED:
      return Object.assign({}, state, {
        __DB__: Object.assign({}, state.__DB__, {
          [action.data._id]: action.data,
        }),
        ids: [...state.ids, action.data._id],
      });

    case DOCUMENT_UPDATED:
      return Object.assign({}, state, {
        __DB__: Object.assign({}, state.__DB__, {
          [action.id]: action.data,
        }),
      });

    case DOCUMENT_DELETED:
      return Object.assign({}, state, {
        __DB__: Object.assign({}, state.__DB__, {
          [action.id]: undefined,
        }),
        ids: state.ids.filter(id => id !== action.id),
      });

    default:
      return state;
  }
}

export default function page(state = initialState, action) {
  switch (action.type) {
    case MODEL_DATA_LOADED:
    case DOCUMENT_CREATED:
    case DOCUMENT_UPDATED:
    case DOCUMENT_DELETED: {
      const key = pluralize(action.modelName);
      return Object.assign({}, state, {
        [key]: model(state[key], action),
      });
    }

    default:
      return state;
  }
}

function handleErrors(res) {
  if (!res.ok) {
    return Promise.reject(res);
  }
  return Promise.resolve(res);
}

export const fetchModelData = modelName => (dispatch) => {
  const ADMIN_SERVER_HOST = nconf.get('ADMIN_SERVER_HOST');
  return fetch(`http://${ADMIN_SERVER_HOST}/api/modelData/${pluralize(modelName)}`, {
    credentials: 'same-origin',
    method: 'GET',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
    },
  }).then(handleErrors)
    .then(res => res.json())
    .then(data => dispatch({
      type: MODEL_DATA_LOADED,
      modelName,
      data,
    }));
};

export const createDocument = (modelName, document) => (dispatch) => {
  const ADMIN_SERVER_HOST = nconf.get('ADMIN_SERVER_HOST');
  return fetch(`http://${ADMIN_SERVER_HOST}/api/modelData/${pluralize(modelName)}/create`, {
    credentials: 'same-origin',
    method: 'POST',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(document),
  }).then(handleErrors).then(res => res.json()).then(data => dispatch({
    type: DOCUMENT_CREATED,
    modelName,
    data,
  }));
};

export const updateDocument = (modelName, id, document) => (dispatch) => {
  const ADMIN_SERVER_HOST = nconf.get('ADMIN_SERVER_HOST');
  return fetch(`http://${ADMIN_SERVER_HOST}/api/modelData/${pluralize(modelName)}/${id}/update`, {
    credentials: 'same-origin',
    method: 'POST',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(document),
  }).then(handleErrors).then(res => res.json()).then(data => dispatch({
    type: DOCUMENT_UPDATED,
    modelName,
    id,
    data,
  }));
};

export const deleteDocument = (modelName, id) => (dispatch) => {
  const ADMIN_SERVER_HOST = nconf.get('ADMIN_SERVER_HOST');
  return fetch(`http://${ADMIN_SERVER_HOST}/api/modelData/${pluralize(modelName)}/${id}/delete`, {
    credentials: 'same-origin',
    method: 'POST',
    headers: {
      Accept: 'application/json',
      'Content-Type': 'application/json',
    },
  }).then(handleErrors).then(res => res.json()).then(data => dispatch({
    type: DOCUMENT_DELETED,
    modelName,
    id,
    data,
  }));
};

const initialState = {};

export const POST_NOTIFICATION = 'POST_NOTIFICATION';
export const CLEAR_NOTIFICATION = 'CLEAR_NOTIFICATION';

export const ERROR = 'ERROR';
export const SUCCESS = 'SUCCESS';
export const WARNING = 'WARNING';
export const INFO = 'INFO';
export const DANGER = 'DANGER';

export default function notifications(state = initialState, action) {
  switch (action.type) {
    case POST_NOTIFICATION:
      return Object.assign({}, state, {
        [action.key]: {
          status: action.status,
          message: action.message,
        },
      });

    case CLEAR_NOTIFICATION:
      return (() => {
        const s = Object.assign({}, state);
        delete s[action.key];
        return s;
      })();

    default:
      return state;
  }
}

let KEY = 0;

export function postNotification(status, message) {
  return {
    type: POST_NOTIFICATION,
    key: KEY++,
    status,
    message,
  };
}

export function clearNotification(key) {
  return {
    type: CLEAR_NOTIFICATION,
    key,
  };
}

